mapper.select:
SELECT *
FROM t_bing_image t1
         JOIN (
    SELECT ROUND(RAND() * ((
                               SELECT MAX(id)
                               FROM t_bing_image
                           ) - (
                               SELECT MIN(id)
                               FROM t_bing_image
                           )) + (
                     SELECT MIN(id)
                     FROM t_bing_image
                 )) AS id
) t2
WHERE t1.id >= t2.id
ORDER BY t1.id LIMIT 1;

SELECT *
FROM t_bing_image
WHERE year = #{year}
  AND month = #{month}
  AND day = #{day} LIMIT 1
